import React, { useState } from 'react'


// FullCalendarコンポーネント。
import FullCalendar from '@fullcalendar/react'

// で週表示を可能にするモジュール。
import timeGridPlugin from '@fullcalendar/timegrid'

// で月表示を可能にするモジュール。
import dayGridPlugin from '@fullcalendar/daygrid'

// で日付や時間が選択できるようになるモジュール。
import interactionPlugin from '@fullcalendar/interaction'

import DatePicker, { registerLocale } from "react-datepicker";

import ja from 'date-fns/locale/ja'

import { createStyles, makeStyles } from "@material-ui/core/styles";

// import "@fullcalendar/core/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";

const CalendarData = (props) => {

  return (
    <FullCalendar
      locale="ja" // 日本語
      defaultView="timeGridWeek" // 基本UI
      slotDuration="00:30:00" // 表示する時間軸の最小値
      selectable={true} // 日付選択可能
      allDaySlot={false} // alldayの表示設定
      titleFormat={{
        year: "numeric",
        month: "short",
        day: "numeric",
      }} // タイトルに年月日を表示
      header={{
        left: "prev,next,today",
        center: "title",
        right: "dayGridMonth,timeGridWeek",
      }}
      businessHours={{
        daysOfWeek: [1, 2, 3, 4, 5],
        startTime: "0:00",
        endTime: "24:00",
      }}
      plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
      ref={this.ref}
      weekends={true} // 週末表示
      events={this.myEvents} // 起動時に登録するイベント
      select={this.handleSelect} // カレンダー範囲選択時
      eventClick={this.handleClick} // イベントクリック時
    />

  )
}



export default CalendarData;
